INSERT IGNORE INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001946,'This is it, $n. The start of the end of the world. And you\'re here to see it off...',0,0,0,1,NULL),
(2000001947,'Let\'s go...',0,0,0,0,NULL),
(2000001948,'Is this it? Is this all the mighty Horde could muster?',0,0,0,6,NULL),
(2000001949,'Pathetic.',0,0,0,11,NULL),
(2000001950,'I\'ve never understood your type. The hero...',0,0,0,1,NULL),
(2000001951,'Why won\'t you just let go? Why do you fight the inevitable?',0,0,0,6,NULL),
(2000001952,'The Lich King cannot be stopped. Accept it.',0,0,0,274,NULL),
(2000001953,'This world is coming to an end. Let. It. Burn.',0,0,0,397,NULL),
(2000001954,'Take $g him:her; away and prepare $g him:her; for reanimation.',0,0,0,1,NULL),
(2000001955,'I\'ll rip your shriveled heart out with my bare hands before any harm comes to $n, necromancer.',0,0,0,5,NULL),
(2000001956,'What\'s this now?',0,0,0,1,NULL),
(2000001957,'You were never alone, $n.',0,0,0,1,NULL),
(2000001958,'This world that you seek to destroy is our home.',0,0,0,1,NULL),
(2000001959,'We will fight you with every fiber of our being - until we are nothing more than dust and debris. We will fight until the end.',0,0,0,1,NULL),
(2000001960,'Then you are a fool.',0,0,0,25,NULL),
(2000001961,'A fool who is about to end you, necrolord. There will be nothing left of you for the Lich King to reanimate!',0,0,0,397,NULL),
(2000001962,'You? A lone orc? Against me and...',0,0,0,397,NULL),
(2000001963,'RISE!',0,1,0,0,NULL),
(2000001964,'I... I can\'t believe it... Saurfang... I... I am honored... honored to have fought along side you, sir.',0,0,0,2,NULL),
(2000001965,'You should return to Warsong Hold now, friend. Hellscream will surely want to hear of what just happened here... of Saurfang...',0,0,0,1,NULL),
(2000001966,'You\'ll make no mention of me. Either of you!',0,0,0,397,NULL),
(2000001967,'%s nods.',0,2,0,0,NULL),
(2000001968,'Farewell...',0,0,0,3,NULL);
