INSERT IGNORE INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001994, 'We are surrounded... Our enemies press in from all sides, young Hellscream.', 0, 0, 0, 1, NULL),
(2000001995, 'The Scourge descends like locust from the north.', 0, 0, 0, 1, NULL),
(2000001996, 'The Alliance holds the only secure shipping lane in this region, and even that is at risk of being lost to those dreaded mists.', 0, 0, 0, 25, NULL),
(2000001997, 'Our only viable port for resupply is held by the Forsaken on the other side of this blasted continent!', 0, 0, 0, 1, NULL),
(2000001998, 'Anything our zeppelins cannot haul must be brought in by ship and travel the length of Northrend to reach us.', 0, 0, 0, 1, NULL),
(2000001999, '%s grunts.', 0, 2, 0, 0, NULL),
(2000002000, 'Shipping lanes... supplies... You bore me to death! We need nothing more than the warrior spirit of the Horde, Saurfang! Now that we are firmly entrenched in this frozen wasteland, nothing shall stop us!', 0, 0, 0, 378, NULL),
(2000002001, 'Siege engines, ammunition, heavy armor... How do you propose to shatter the walls of Icecrown without those?', 0, 0, 0, 378, NULL),
(2000002002, 'Propose? I will show you what I propose!', 0, 0, 0, 6, NULL),
(2000002003, 'There... Now we now have a shipping lane.', 0, 0, 0, 1, NULL),
(2000002004, 'And just for good measure...', 0, 0, 0, 5, NULL),
(2000002005, 'So the prodigal son has spoken!', 0, 0, 0, 5, NULL),
(2000002006, 'Your father\'s blood runs strong in you, Hellscream. Impatient as always... Impatient and reckless.', 0, 0, 0, 378, NULL),
(2000002007, 'You rush headlong into all-out war without a thought of the consequences.', 0, 0, 0, 25, NULL),
(2000002008, 'Do not speak to me of consequences, old one.', 0, 0, 0, 25, NULL),
(2000002009, 'I drank of the same blood your father did, Garrosh. Mannoroth\'s cursed venom pumped through my veins as well.', 0, 0, 0, 378, NULL),
(2000002010, 'I drove my weapons into the bodies and minds of my enemies.', 0, 0, 0, 0, NULL),
(2000002011, 'And while Grom died a glorious death - freeing us all from the blood curse - he could not wipe away the terrible memory of our past.', 0, 0, 0, 0, NULL),
(2000002012, 'His act could not erase the horrors we committed.', 0, 0, 0, 274, NULL),
(2000002013, '%s pauses.', 0, 2, 0, 0, NULL),
(2000002014, 'The winter after the curse was lifted, hundreds of veteran orcs like me were lost to despair.', 0, 0, 0, 378, NULL),
(2000002015, 'Our minds were finally free, yes... Free to relive all of the unthinkable acts that we had performed under the Legions\' influence.', 0, 0, 0, 0, NULL),
(2000002016, '%s nods.', 0, 2, 0, 0, NULL),
(2000002017, 'I think it was the sounds of the draenei children that unnerved most of them... You never forget...', 0, 0, 0, 1, NULL),
(2000002018, 'Have you ever been to Jaggedswine Farm? When the swine are of age for the slaughter... It\'s that sound. The sound of the swine being killed... It resonates the loudest. Those are hard times for us older veterans.', 0, 0, 0, 6, NULL),
(2000002019, 'But surely you cannot think that those children were born into innocence? They would have grown up and taken arms against us!', 0, 0, 0, 5, NULL),
(2000002020, '%s shakes his head.', 0, 2, 0, 274, NULL),
(2000002021, 'I am not speaking solely of the children of our enemies...', 0, 0, 0, 1, NULL),
(2000002022, 'I won\'t let you take us down that dark path again, young Hellscream. I\'ll kill you myself before that day comes...', 0, 0, 0, 274, NULL),
(2000002023, 'How have you managed to survive for so long, Saurfang? Not fallen victim to your own memories?', 0, 0, 0, 6, NULL),
(2000002024, 'I don\'t eat pork...', 0, 0, 0, 274, NULL),
(2000002025, '%s spits.', 0, 2, 0, 0, NULL);
