INSERT IGNORE INTO `db_script_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES
(2000002058, 'Children of the Night, heed your Queen\'s call!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 22, 'Banshee Queen Speech Part 1'),
(2000002059, 'I join you in celebration of this most revered of nights - the night we Forsaken broke the Scourge\'s yoke of oppression!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Banshee Queen Speech Part 2'),
(2000002060, 'It is this night that our enemies fear us the most. It is THIS night that we show our enemies what it means to stand against the Forsaken!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Banshee Queen Speech Part 3'),
(2000002061, 'We burn the effigy of the Wickerman as a symbol of our struggle against those who would oppose us. We wear the ashes of the burnt Wickerman as a symbol of our neverending fight against those who would enslave us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Banshee Queen Speech Part 4'),
(2000002062, 'Now is the time to shake the world to its foundations! NOW is the time to remind those who would enslave us that we shall never yield!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 5, 'Banshee Queen Speech Part 5'),
(2000002063, 'NOW is the time of the Forsaken! Power to the Forsaken - NOW AND FOREVER!!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 15, 'Banshee Queen Speech Part 6'),
(2000002064, 'The burning of the Wickerman will begin in thirty minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Yanka 30 Mins to Festival Start'),
(2000002065, 'The burning of the Wickerman will begin in fifteen minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Yanka 15 Mins to Festival Start'),
(2000002066, 'The burning of the Wickerman will begin in five minutes! Come to the festival - located just west of the Undercity - and celebrate Hallow\'s End and our liberation from the Scourge!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Yanka 5 Mins to Festival Start'),
(2000002067, 'The Wickerman Festival now begins! Power to the Forsaken!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 1, 1, 'Yanka Festival Start');
