INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES 
(2000002179,'Keller\'s soldiers ain\'t brought me nothin\' in days... No news is good news I \'spose.',0,0,0,0,NULL),
(2000002180,'Oy! Keller! Any word from the ruins? Anything at all??',0,0,0,6,NULL),
(2000002181,'I\'ve already told you, McSorf. I\'ve sent no less than three rescue teams into the ruins and none have returned. I am working tirelessly to keep this port afloat and in one piece! GET OFF MY BACK!',0,0,0,5,NULL);
