INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES 
(2000002182,'%s clears his throat.',0,2,0,0,NULL),
(2000002183,'I look across this base and all I see is fear... Are you gonna be scared when the Lich King\'s armies are sittin\' pretty in Stormwind? Ironforge? NO! You\'re not. And do you know why? Because you\'ll be dead, that\'s why.',0,0,0,1,NULL),
(2000002184,'So when you\'re feeling that tinglin\' sensation at the base of your spine and you\'re \'bout ready to wet your pants, embrace it. Let it enrage you. That fear is the only thing that\'s gonna keep you breathin\'.',0,0,0,1,NULL),
(2000002185,'Am I clear? Do you understand what I\'m sayin\'? I don\'t wanna have to send any more coffins or letters home.',0,0,0,1,NULL);
