INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES 
(2000002271,'Sure is busy here Celidh.  Do you have any pets you want me to move to the stables?',0,0,0,0,NULL),
(2000002272,'All these folks are keeping me busy.  Take this horse over to your stables.',0,0,0,396,NULL),
(2000002273,'You\'re such a sweet lad.  Here, take this horse over to your stable.',0,0,0,396,NULL),
(2000002274,'Let\'s go boy.',0,0,0,0,NULL),
(2000002275,'See this statue, boy?  This is the king of Stormwind, Varian Wrynn.',0,0,0,0,NULL),
(2000002276,'His son, Anduin Wrynn, was the king for a while.  He\'s about my age too.  Can you imagine if I were the king?',0,0,0,0,NULL),
(2000002277,'People used to come up to me because they thought I was the king.  I guess we look alike...',0,0,0,0,NULL),
(2000002278,'If I were king, I\'d have a horse like you.  I\'d make sure you didn\'t get lost, and I bet you\'d make sure I never got lost.',0,0,0,0,NULL),
(2000002279,'We\'d be best friends.  I bet me and Anduin would also be best friends, too.  Friends are important.',0,0,0,0,NULL),
(2000002280,'I\'ll take you to some other horses.  You probably miss being around other horses.  They can be your friend too.',0,0,0,0,NULL);
