INSERT INTO dbscript_string (entry, content_default, emote) VALUES
(2000002328,'Fascinating. What is it, Andormu?',6),
(2000002329,'Something very, very disturbing. We''re starting to see more of them across the timeways.',1),
(2000002330,'Hrm, are they tampering with timeways?',6),
(2000002331,'Nozari, I''m not talking about a greedy wizard looking to improve his station in life by making a small adjustment to the past.',274),
(2000002332,'These beings aren''t giving themselves the winning numbers for the Stormwind lottery, dear.',1),
(2000002333,'I hate it when you speak to me in that manner. I am your equal in every way, brother. Remember that the next time you are trapped amidst a temporal vortex, crying for help.',1),
(2000002334,'My apologies, Nozari. I have been a bit ''on edge'' as of late. These creatures are attempting to alter the timeways.',273),
(2000002335,'You do recall the last time that this occurred, yes? Let us hope that the master need not be involved.',1),
(2000002336,'Shall we?',6),
(2000002337,'Absolutely not. We must not place ourselves at risk. With the master away, we are all that is in place to keep the stream intact. Others are due to arrive any moment now. Heroes and adventurers from Azeroth...',1);
