INSERT INTO `dbscript_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES 
(2000002343, 'My master, we are honored by your presence.  How may I serve thee?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Sharth Voldoun (Entry: 18554)'),
(2000002344, 'Rise, young Voldoun.  Do not worry yourself with pleasantries.  I come at the behest of our master, Prince Kael''thas.  He bids me to check on the progress of the mana bomb.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Pathaleon the Calculator''s Image (Entry: 21504)'),
(2000002345, 'All goes exceedingly well, my lord.  Testing of the smaller prototype at the Cenarion Thicket was a complete success.  The second bomb is being ritually fueled in the courtyard below even as we speak.  And, I''ve sent a courier to Tuurem to bring the rest of the parts to us here.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Sharth Voldoun (Entry: 18554)'),
(2000002346, 'You are satisfied?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'Sharth Voldoun (Entry: 18554)'),
(2000002347, 'For the time being, yes.  However, allow my presence to be a motivator.  Prince Kael''thas was displeased with the failure of the crystal experiment on Fallen Sky Ridge.  This is one of the reasons for why we chose the Cenarion druids as the testing grounds for the bomb.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Pathaleon the Calculator''s Image (Entry: 21504)'),
(2000002348, 'I need not tell you what will happen should the mana bomb down in the courtyard fail to be used on its target soon?  Since moving into the forest, they''ve become increasingly annoying to our operations: here, at Tuurem and to the south at the Bonechewer Ruins.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'Pathaleon the Calculator''s Image (Entry: 21504)'),
(2000002349, 'I think that we should teach a lesson to both the Horde and the Alliance.  One that they will not soon forget!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'Pathaleon the Calculator''s Image (Entry: 21504)'),
(2000002350, 'I can assure you that we will not fail, my master.  I am personally overseeing every aspect of the construction, and I hold the final codes, myself.  Within a day''s time, I will have the bomb detonated on those nearby pests.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 2, 'Sharth Voldoun (Entry: 18554)'),
(2000002351, 'See to it that you do, Sharth, or I will personally see to your slow torture and death.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'Pathaleon the Calculator''s Image (Entry: 21504)'),
(2000002352, 'I believe I may recognize them.  Deal with this quickly, Sharth.  Then take the mana bomb and destroy their town!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'Pathaleon the Calculator''s Image (Entry: 21504)');
